mapper.select:
select study_title, study_regist_date from tbl_study
where id in (
	select study_id from tbl_study_applicant
	where study_applicant_id = #{id}
)

select study_title, study_regist_date, study_status, study_total from tbl_study
where id in (
	select study_id from tbl_study_applicant
	where study_applicant_id = #{id}
) or member_id = #{id}
